mapper.select:
select
  isoid, displayname, filepath, displayostype, pveostype
from IsoFile


        and isoid = #{isoid}


        and displayname = #{displayname}


        and filepath = #{filepath}


        and displayostype = #{displayostype}


        and pveostype = #{pveostype}

select count(1)
from IsoFile


        and isoid = #{isoid}


        and displayname = #{displayname}


        and filepath = #{filepath}


        and displayostype = #{displayostype}


        and pveostype = #{pveostype}
